        pNode->pNext = pTmp;

        pTmp->pFront->pNext = pNode;
        pTmp->pFront = pNode;

        m_nSize ++;
    }

    void eraseAt(int nIndex)
    {
        if(m_pHead == NULL) return;
        if(nIndex >= m_nSize) return;

        // 删除第一个节点 需要改变头指针的位置
        if(nIndex == 0)
        {
            Node* pTmp = m_pHead;
            // 只有一个节点
            if(m_nSize == 1)
            {
                m_pHead = m_pEnd = NULL;
            }
            else
            {
                m_pHead = m_pHead->pNext;
                m_pHead->pFront = NULL;
            }
            delete pTmp;
            m_nSize--;
            return;
        }

        // 删除最后一个节点 需要改变尾部指针的位置
        if(nIndex == m_nSize - 1)
        {
            Node* pTmp = m_pEnd;
            m_pEnd = m_pEnd->pFront;
            m_pEnd->pNext = NULL;
            m_nSize--;
            delete pTmp;
            return;
        }

        int nCount = 0;
        Node* pTmp = m_pHead;
        while (nCount pNext;
            nCount++;
        }

        pTmp->pFront->pNext = pTmp->pNext;
        pTmp->pNext->pFront = pTmp->pFront;
        delete pTmp;
        m_nSize--;

    }

    T getAt(int nIndex)
    {
