    void clear()
    {
        if(m_pHead == NULL) return;

        Node* pNode = m_pHead;
        while (pNode != NULL) {
            Node* pTmp = pNode;
            pNode = pNode->pNext;
            delete pTmp;
        }

        m_pHead->pNext = NULL;
        m_pEnd->pFront = NULL;
        m_pHead = m_pEnd = NULL;

        m_nSize = 0;
    }


